(function(pm) {
    pm.sendRequest({
        url: pm.variables.replaceIn("{{BASE_URL}}users/login"),
        method: 'POST',
        header: { 'Content-Type': 'application/json' },
        body: {
            mode: 'raw',
            raw: JSON.stringify({
                user: { 
                    email: pm.collectionVariables.get("email"), 
                    password: pm.collectionVariables.get("password") 
                }
            })
        }
    }, (err, res) => {
        if (res) {
            const token = res.json().user.token; 
            pm.collectionVariables.set("token", token);
        }
    });
})(pm);

(function(pm) {
