        url: pm.variables.replaceIn("{{BASE_URL}}articles"),
        method: 'POST',
        header: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + token
        },
        body: {
            mode: 'raw',
            raw: JSON.stringify({
                article: {
                    title: pm.variables.replaceIn("{{$randomWord}}"),
                    description: pm.variables.replaceIn("{{$randomWords}}"),
                    body: pm.variables.replaceIn("{{$randomWords}}{{$randomWords}}{{$randomWords}}"),
                    tagList: []
                }
            })
        }
    }, (err, res) => {
        if (res) {
            const slug = res.json().article.slug;
